SELECT DISTINCT ?Dataset ?Handle 
 WHERE { 
  ?Dataset rdf:type ontomedirad:CT_image_dataset .
  OPTIONAL { ?Dataset ontomedirad:has_IRDBB_WADO_handle ?Handle .}
 } ORDER BY ?Dataset

SELECT DISTINCT ?Human ?PatientName ?PatientId ?PatientSex ?PatientBirthDate 
 WHERE {
   ?Human rdf:type ontomedirad:human .
   ?Human ontomedirad:has_id ?PatientId .
   OPTIONAL { ?Human ontomedirad:has_sex ?PatientSex .}
   OPTIONAL { ?Human ontomedirad:has_name ?PatientName .}
   OPTIONAL { ?Human ontomedirad:has_birthdate ?PatientBirthDate . }
}

SELECT DISTINCT ?StudyDescr ?ProtocolName ?ProtocolDescr ?ExamDate ?ExamTime ?PatientName ?LabelSex ?AgeInYears ?ValueWeight ?LabelUnitWeight ?CTAcqClass ?ValueKVP ?LabelUnitKVP ?ValueTubeCur ?LabelUnitTubeCur ?LabelXRayModulationType ?Dataset ?DatasetClassLabel ?ImageTypeDescription ?DatasetHandle ?Model ?Manufacturer WHERE { ?RolePat purl:BFO_0000054 ?Exam . ?Human purl:BFO_0000087 ?RolePat . ?CTAcq purl:BFO_0000132 ?Exam . ?CTAcq rdf:type ?CTAcqClass . ?CTAcqClass rdfs:subClassOf* ontomedirad:CT_acquisition . ?Dataset ontomedirad:is_specified_output_of ?CTAcq . ?Dataset ontomedirad:has_DICOM_image_type_description ?ImageTypeDescription . ?Dataset rdf:type ?Datasetclass .  ?Datasetclass skos:prefLabel ?DatasetClassLabel .
 OPTIONAL { ?CTAcq ontomedirad:has_protocol ?Protocol . 	    OPTIONAL { ?Protocol ontomedirad:has_name ?ProtocolName .}	    ?Protocol ontomedirad:has_description ?ProtocolDescr . }
 OPTIONAL { ?Exam ontomedirad:has_beginning_date ?ExamDate .            ?Exam ontomedirad:has_beginning_time ?ExamTime . } OPTIONAL { ?Exam ontomedirad:has_description ?StudyDescr } OPTIONAL { ?Human ontomedirad:has_sex ?PatientSex .            ?PatientSex rdfs:label ?LabelSex . } OPTIONAL { ?Human ontomedirad:has_name ?PatientName } OPTIONAL { ?PatientAge rdf:type ontomedirad:age_of_patient_undergoing_medical_procedure .            ?PatientAge ontomedirad:is_about_procedure ?Exam .            ?PatientAge ontomedirad:is_about ?Human .            ?PatientAge ontomedirad:years ?AgeInYears . } OPTIONAL { ?PatientWeight rdf:type ontomedirad:patient_weight .            ?PatientWeight ontomedirad:is_about_procedure ?Exam .            ?PatientWeight ontomedirad:is_about ?Human .            ?PatientWeight purl:IAO_0000004 ?ValueWeight .            ?PatientWeight purl:IAO_0000039 ?UnitWeight .            ?UnitWeight rdfs:label ?LabelUnitWeight . } OPTIONAL { ?CTAcq ontomedirad:has_protocol ?AcqProtocol .            ?XRayModulationType purl:BFO_0000177 ?AcqProtocol .            ?XRayModulationType rdfs:subClassOf dcm:113842 .            ?XRayModulationType skos:prefLabel ?LabelXRayModulationType . }                               OPTIONAL {?CTAcq ontomedirad:has_setting ?KVP .  ?KVP rdf:type dcm:113733 .  ?KVP purl:IAO_0000004 ?ValueKVP .  ?KVP purl:IAO_0000039 ?UnitKVP .  ?UnitKVP rdfs:label ?LabelUnitKVP.} OPTIONAL {?CTAcq ontomedirad:has_setting ?TubeCur .  ?TubeCur rdf:type dcm:113734 .  ?TubeCur purl:IAO_0000004 ?ValueTubeCur .  ?TubeCur purl:IAO_0000039 ?UnitTubeCur.  ?UnitTubeCur rdfs:label ?LabelUnitTubeCur .} OPTIONAL {?Dataset ontomedirad:has_IRDBB_WADO_handle ?DatasetHandle .} OPTIONAL { ?Scanner rdf:type ontomedirad:CT_scanner .  ?AcqRole rdf:type ontomedirad:image_acquisition_role .  ?Scanner purl:BFO_0000087 ?AcqRole.       ?AcqRole purl:BFO_0000054 ?CTAcq .  ?Scanner ontomedirad:has_manufacturer_name ?Manufacturer .  ?Scanner ontomedirad:has_model_name ?Model .}} ORDER BY ?PatientName

SELECT DISTINCT ?StudyDescr ?ProtocolName ?ProtocolDescr ?ExamDate ?ExamTime ?PatientName ?LabelSex ?AgeInYears ?ValueWeight ?LabelUnitWeight ?RadioNuclLabel ?LowerLimit ?UpperLimit ?LabelUnitEnergyWindow ?Dataset ?DatasetClassLabel ?ImageTypeDescription ?DatasetHandle ?Model ?Manufacturer
 WHERE {
 ?PETAcq rdf:type* ontomedirad:PET_data_acquisition .
 ?PETAcq purl:BFO_0000132 ?Exam .
 ?RolePat purl:BFO_0000054 ?Exam .
 ?Human purl:BFO_0000087 ?RolePat .
 ?Dataset ontomedirad:is_specified_output_of ?PETAcq .
 ?Dataset ontomedirad:has_DICOM_image_type_description ?ImageTypeDescription .
 ?Dataset rdf:type ontomedirad:PET_recon_tomo_dataset .
 OPTIONAL { ?PETAcq ontomedirad:has_protocol ?Protocol . 	    OPTIONAL { ?Protocol ontomedirad:has_name ?ProtocolName .}	    ?Protocol ontomedirad:has_description ?ProtocolDescr . }
 OPTIONAL { ?Exam ontomedirad:has_beginning_date ?ExamDate .
            ?Exam ontomedirad:has_beginning_time ?ExamTime . }
 OPTIONAL {?Exam ontomedirad:has_description ?StudyDescr }
 OPTIONAL {?PETAcq rdfs:subClassOf* ontomedirad:PET_data_acquisition}
 OPTIONAL { ?Human ontomedirad:has_name ?PatientName }
 OPTIONAL { ?PatientWeight rdf:type ontomedirad:patient_weight .
            ?PatientWeight ontomedirad:is_about_procedure ?Exam .
            ?PatientWeight ontomedirad:is_about ?Human .
            ?PatientWeight purl:IAO_0000004 ?ValueWeight .
            ?PatientWeight purl:IAO_0000039 ?UnitWeight .
            ?UnitWeight rdfs:label ?LabelUnitWeight . }
 OPTIONAL { ?Human ontomedirad:has_sex ?PatientSex .
            ?PatientSex rdfs:label ?LabelSex . }
 OPTIONAL { ?PatientAge rdf:type ontomedirad:age_of_patient_undergoing_medical_procedure .
            ?PatientAge ontomedirad:is_about_procedure ?Exam .
            ?PatientAge ontomedirad:is_about ?Human .
            ?PatientAge ontomedirad:years ?AgeInYears . }
 OPTIONAL {?PETAcq ontomedirad:has_target_radionuclide ?RadioNucl .
           ?RadioNucl rdfs:label ?RadioNuclLabel .}
 OPTIONAL {?PETAcq ontomedirad:has_setting ?EnergyWindow .
	  	 ?EnergyWindow purl:BFO_0000177 ?AcqProtocol .
                 ?EnergyWindow ontomedirad:has_lower_limit ?LowerLimit .
                 ?EnergyWindow ontomedirad:has_upper_limit ?UpperLimit .
                 ?EnergyWindow purl:IAO_0000039 ?UnitEnergyWindow .
                 ?UnitEnergyWindow rdfs:label ?LabelUnitEnergyWindow }
 OPTIONAL {?Dataset ontomedirad:has_IRDBB_WADO_handle ?DatasetHandle .}
 OPTIONAL { ?Scanner rdf:type ontomedirad:PET_scanner .
  ?AcqRole rdf:type ontomedirad:image_acquisition_role .
  ?Scanner purl:BFO_0000087 ?AcqRole.     
  ?AcqRole purl:BFO_0000054 ?PETAcq .
  ?Scanner ontomedirad:has_manufacturer_name ?Manufacturer .
 ?Scanner ontomedirad:has_model_name ?Model .}
} ORDER BY ?PatientName

